"use strict";var windowTop=window.pageYOffset+3*window.innerHeight/6,target=document.querySelectorAll("[data-anime]");function animaScroll(){target.forEach(function(e){var o=e.getBoundingClientRect();windowTop>o.top?e.classList.add("animate"):e.classList.remove("animate")})}window.addEventListener("scroll",animaScroll);var mainNav=document.getElementById("main-nav"),toggleMenu=document.getElementById("toggle-menu");function mostrarOcultar(){mainNav.classList.toggle("mostrar")}function ocultarMenu(){mainNav.classList.remove("mostrar")}document.addEventListener("click",function(e){e.target!==mainNav&&e.target!==toggleMenu&&ocultarMenu()}),toggleMenu.addEventListener("click",mostrarOcultar);var header=document.querySelector("#mainHeader"),logoContainer=document.querySelector("#logoContainer"),logoText=document.querySelector("#logoText");function addHeaderColor(){50<=window.scrollY?(header.classList.add("header-color"),logoContainer.classList.add("logo-onScroll"),logoText.classList.add("logo-text-onScroll")):(header.classList.remove("header-color"),logoContainer.classList.remove("logo-onScroll"),logoText.classList.remove("logo-text-onScroll"))}window.addEventListener("scroll",addHeaderColor);